cpanspec.post_install:
# avoid conflict with Perl's own supplied version
mv %{buildroot}/usr/bin/config_data %{buildroot}/usr/bin/config_data-%{version}
rename config_data config_data-%{version} %{buildroot}/%{_mandir}/man1/config_data.*

cpanspec.custom_build:
export BUILDING_AS_PACKAGE=1
%{__perl} Makefile.PL INSTALLDIRS=vendor
%{__make} %{?_smp_mflags}

yes no | %__perl ./Build.PL installdirs=vendor
yes no | ./Build
./Build build flags=%{?_smp_mflags}

# fails to detect the paths since perl 5.32
perl Makefile.PL verbose INSTALLDIRS=vendor OPTIMIZE="%{optflags}" --libs="-L%{_libdir} -lmariadb" --cflags="-I%{_includedir}/mysql"
make %{?_smp_mflags}

cpanspec.custom_test:
# Setup environment and start database
. %{SOURCE1}
HARNESS_OPTIONS=j4 make %{?_smp_mflags} test
# Stop database
. %{SOURCE2}

